    /*
     * Get login by ID_TBL_LOGIN
     */
    function get_login($ID_TBL_LOGIN)
    {
        return $this->db->get_where('TBL_LOGIN',array('ID_TBL_LOGIN'=>$ID_TBL_LOGIN))->row_array();
    }
        
    /*
     * Get all logins
     */
    function get_all_logins()
    {
        $this->db->order_by('ID_TBL_LOGIN', 'desc');
        return $this->db->get('TBL_LOGIN')->result_array();
    }
        
