    {
        $this->db->insert('TBL_LOGIN',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update login
     */
    function update_login($ID_TBL_LOGIN,$params)
    {
        $this->db->where('ID_TBL_LOGIN',$ID_TBL_LOGIN);
        return $this->db->update('TBL_LOGIN',$params);
    }
    
    /*
     * function to delete login
     */
    function delete_login($ID_TBL_LOGIN)
    {
        return $this->db->delete('TBL_LOGIN',array('ID_TBL_LOGIN'=>$ID_TBL_LOGIN));
    }

    function check_user($table,$where)
